var DescriptionsCollection=function(i){this.professions=i,this.descriptions=this.makeCollection(),this.randomArrayIndex=new randomArrayIndex(this.descriptions)};DescriptionsCollection.prototype.makeCollection=function(){for(var i=[],t=0;t<this.professions.length;t++){var o=new Description(t,this.professions[t]);i.push(o)}return i},DescriptionsCollection.prototype.turnCard=function(){var i=this.descriptions[this.randomArrayIndex.get()];return i.element.querySelector(".card").classList.add("flip"),i};