var gridWidth=100,gridHeight=200,cards=document.querySelectorAll(".box"),answers=document.querySelectorAll(".answer"),correctAnswers=0,numberOfAnswers=0;[].forEach.call(cards,function(e){Draggable.create(e,{type:"x,y",edgeResistance:.65,bounds:".professions",throwProps:!0,snap:{x:function(e){return Math.round(e/gridWidth)*gridWidth},y:function(e){return Math.round(e/gridHeight)*gridHeight}},onDragEnd:function(e){[].forEach.call(answers,function(e){if(this.hitTest(e,50)){if(numberOfAnswers++,document.getElementById("number-of-answers").innerHTML=numberOfAnswers,e.getAttribute("data-pair")===this.target.getAttribute("data-pair")&&correctAnswers++,numberOfAnswers===answers.length){console.log("Antal rätt: "+correctAnswers);var t=document.createElement("p");t.textContent="Antal rätt: "+correctAnswers,document.getElementById("playground").appendChild(t)}this.disable()}}.bind(this))}})});